/**
  * @brief  This function handles USARTx global interrupt request.
  * @param  None
  * @retval None
  */

void USART3_IRQHandler(void){
    if ((USART3->SR & USART_FLAG_RXNE) != (u16)RESET){
			i = USART_ReceiveData(USART3);
			if(j == 11){
                CMD_DATABUFF[j] = i;
    			CMD_DATABUFF[j+1] = '\0';
                j = 0;
			}
			else{

				CMD_DATABUFF[j++] = i;
       			CMD_DATABUFF[j] = '\0';
       			if (CMD_DATABUFF[j-1]==';'){
       				USART_CMDFLAG=1;
       				j=0;
       			}

     		}
	}
}

